prompt prompt prompt "**=====================================================================================================**" prompt** **Users Space Overview ** prompt "**=====================================================================================================**" Select owner "OWNERS>5Mb" ,sum(blocks) Total_blocks,round(sum(bytes/(1024*1024*1024)),1) "TotalGb" From dba_segments Group By owner having round(sum(bytes/(1024*1024)),1) > 5 order by 3 desc;
prompt prompt prompt "**=====================================================================================================**" prompt** ** View only locked/expired users-filter out Oracle default Users that come with an instalation ** prompt "**=====================================================================================================**" set feedback on SELECT substr(username,1,10) username, substr(profile,1,10) profile, account_status, created, lock_date, expiry_date FROM dba_users WHERE account_status != 'OPEN' and username not in ('ANONYMOUS' ,'APEX_040200' ,'APEX_PUBLIC_USER' ,'APPQOSSYS' ,'AUDSYS' ,'BI' ,'CTXSYS' ,'DBSNMP' ,'DIP' ,'DVF' ,'DVSYS' ,'EXFSYS' ,'FLOWS_FILES' ,'GSMADMIN_INTERNAL' ,'GSMCATUSER' ,'GSMUSER' ,'HR' ,'IX' ,'LBACSYS' ,'MDDATA' ,'MDSYS' ,'OE' ,'ORACLE_OCM' ,'ORDDATA' ,'ORDPLUGINS' ,'ORDSYS' ,'OUTLN' ,'PM' ,'SCOTT' ,'SH' ,'SI_INFORMTN_SCHEMA' ,'SPATIAL_CSW_ADMIN_USR' ,'SPATIAL_WFS_ADMIN_USR' ,'SYSBACKUP' ,'SYSDG' ,'SYSKM' ,'WMSYS' ,'XDB' ,'OWBSYS' ,'OWBSYS_AUDIT' ,'APEX_030200' ,'MGMT_VIEW' ,'XS$NULL' ,'SYS$UMF' ,'DBSFWUSER' ,'GGSYS' ,'OLAPSYS' ,'REMOTE_SCHEDULE' ,'REMOTE_SCHEDULER_AGENT' ,'SYSRAC' ,'OJVMSYS'); set feedback off prompt prompt prompt "**=====================================================================================================**" prompt** **Tablespace/File Overview ** prompt "**=====================================================================================================**" Select RPAD(tablespace_name,18)||' '||RTRIM(file_name) Tablespace From dba_data_files Order By tablespace_name;
col "Tablespace" for a32 col "Used MB" for 9,999,999.99 col "Free MB" for 9,999,999.99 col "Total MB" for 9,999,999.99 col extendable_free_space for 9,999,999.99 col "Pct. Free" for 999,999.99 col "maxspace" for 999,999.99 set linesize 200
select df.tablespace_name "Tablespace", nvl(totalusedspace,0) "Used MB", (df.totalspace - nvl(tu.totalusedspace,0)) "Free MB", df.totalspace "Total MB", round(100 * ( (df.totalspace - nvl(tu.totalusedspace,0))/ df.totalspace),2) "Pct. Free", nvl(fs.free_space,0) extendable_free_space , round(maxspace,2) maxspace from (select tablespace_name, round(sum(bytes) / 1048576) TotalSpace , sum(maxbytes)/1024/1024 maxspace from dba_data_files group by tablespace_name) df, (select round(sum(bytes)/(1024*1024)) totalusedspace, tablespace_name from dba_segments group by tablespace_name) tu , ( select tablespace_name, round(sum(bytes)/1024/1024 ,2) as free_space from dba_free_space group by tablespace_name ) fs where df.tablespace_name = tu.tablespace_name(+) AND df.tablespace_name = fs.tablespace_name(+) ORDER BY "Pct. Free";
prompt prompt prompt "**=====================================================================================================**" prompt** **Tablespace/CRD File Information** prompt "**=====================================================================================================**" col "Database Size" format a15; col "Free space" format a15; select round(sum(used.bytes) / 1024 / 1024/1024 ) || ' GB' "Database Size", round(free.p / 1024 / 1024/1024) || ' GB' "Free space" from (select bytes from v$datafile union all select bytes from v$tempfile union all select bytes from v$log) used, (select sum(bytes) as p from dba_free_space) free group by free.p;
SELECT a.tablespace_name tablespace_name, ROUND(a.bytes_alloc / 1024 / 1024, 2) megs_alloc, -- ROUND(NVL(b.bytes_free, 0) / 1024 / 1024, 2) megs_free, ROUND((a.bytes_alloc - NVL(b.bytes_free, 0)) / 1024 / 1024, 2) megs_used, ROUND((NVL(b.bytes_free, 0) / a.bytes_alloc) * 100,2) Pct_Free, (case when ROUND((NVL(b.bytes_free, 0) / a.bytes_alloc) * 100,2)<=0 then 'Immediate action required!' when ROUND((NVL(b.bytes_free, 0) / a.bytes_alloc) * 100,2)<5 then 'Critical (<5% free)' when ROUND((NVL(b.bytes_free, 0) / a.bytes_alloc) * 100,2)<15 then 'Warning (<15% free)' when ROUND((NVL(b.bytes_free, 0) / a.bytes_alloc) * 100,2)<25 then 'Warning (<25% free)' when ROUND((NVL(b.bytes_free, 0) / a.bytes_alloc) * 100,2)>60 then 'Waste of space? (>60% free)' else 'OK' end) msg FROM ( SELECT f.tablespace_name, SUM(f.bytes) bytes_alloc, SUM(DECODE(f.autoextensible, 'YES',f.maxbytes,'NO', f.bytes)) maxbytes FROM DBA_DATA_FILES f GROUP BY tablespace_name) a, ( SELECT f.tablespace_name, SUM(f.bytes) bytes_free FROM DBA_FREE_SPACE f GROUP BY tablespace_name) b WHERE a.tablespace_name = b.tablespace_name (+) UNION SELECT h.tablespace_name, ROUND(SUM(h.bytes_free + h.bytes_used) / 1048576, 2), -- ROUND(SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / 1048576, 2), ROUND(SUM(NVL(p.bytes_used, 0))/ 1048576, 2), ROUND((SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / SUM(h.bytes_used + h.bytes_free)) * 100,2), (case when ROUND((SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / SUM(h.bytes_used + h.bytes_free)) * 100,2)<=0 then 'Immediate action required!' when ROUND((SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / SUM(h.bytes_used + h.bytes_free)) * 100,2)<5 then 'Critical (<5% free)' when ROUND((SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / SUM(h.bytes_used + h.bytes_free)) * 100,2)<15 then 'Warning (<15% free)' when ROUND((SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / SUM(h.bytes_used + h.bytes_free)) * 100,2)<25 then 'Warning (<25% free)' when ROUND((SUM((h.bytes_free + h.bytes_used) - NVL(p.bytes_used, 0)) / SUM(h.bytes_used + h.bytes_free)) * 100,2)>60 then 'Waste of space? (>60% free)' else 'OK' end) msg FROM sys.V_$TEMP_SPACE_HEADER h, sys.V_$TEMP_EXTENT_POOL p WHERE p.file_id(+) = h.file_id AND p.tablespace_name(+) = h.tablespace_name GROUP BY h.tablespace_name ORDER BY 1;
col file_name format a70 heading "Datafile Name" col allocated_mb format 999999.99; col used_mob format 999999.99; col free_mb format 999999.99; col tablespace_name format a20; SELECT SUBSTR (df.NAME, 1, 70) file_name, dfs. tablespace_name, df.bytes / 1024 / 1024 allocated_mb, ((df.bytes / 1024 / 1024) - NVL (SUM (dfs.bytes) / 1024 / 1024, 0)) used_mb, NVL (SUM (dfs.bytes) / 1024 / 1024, 0) free_mb, c.autoextensible FROM v$datafile df, dba_free_space dfs, DBA_DATA_FILES c WHERE df.file# = dfs.file_id(+) AND df.file# = c.FILE_ID GROUP BY dfs.file_id, df.NAME, df.file#, df.bytes, dfs.tablespace_name, c.autoextensible ORDER BY file_name;
SELECT TO_CHAR(creation_time, 'RRRR Month') "Year/Month", round(SUM(bytes)/1024/1024/1024) "Datafile Growth Rate in GB" FROM sys.v_$datafile WHERE creation_time < sysdate GROUP BY TO_CHAR(creation_time, 'RRRR Month');
TTI off
prompt prompt "**=====================================================================================================**" prompt** **Report Tablespace < 10% free space** prompt "**=====================================================================================================**" column tablespace_name format a15 heading Tablespace; column sumb format 999,999,999; column extents format 9999; column bytes format 999,999,999,999; column largest format 999,999,999,999; column Tot_Size format 999,999 Heading "Total Size(Mb)"; column Tot_Free format 999,999,999 heading "Total Free(Kb)"; column Pct_Free format 999.99 heading "% Free"; column Max_Free format 999,999,999 heading "Max Free(Kb)"; column Min_Add format 999,999,999 heading "Min space add (MB)"; select a.tablespace_name,sum(a.tots/1048576) Tot_Size,sum(a.sumb/1024) Tot_Free, sum(a.sumb)*100/sum(a.tots) Pct_Free, ceil((((sum(a.tots) * 15) - (sum(a.sumb)*100))/85 )/1048576) Min_Add from (select tablespace_name,0 tots,sum(bytes) sumb from sys.dba_free_space a group by tablespace_name union select tablespace_name,sum(bytes) tots,0 from sys.dba_data_files group by tablespace_name) a group by a.tablespace_name having sum(a.sumb)*100/sum(a.tots) < 10 order by pct_free;
