set pagesize 0; select '+ Weekly (max): '||max(no)||' switches. Size: '||round((max(no) * max(logsize )) ,2)||'MB'||chr(10)|| '+ Weekly (avg): '||trunc(avg(no))||' switches. Size: '||round((avg(no) * max(logsize )) ,2)||'MB'||chr(10)|| '+ Weekly (min): '||min(no)||' switches. Size: '||round((min(no) * max(logsize )) ,2)||'MB' from (select trunc(FIRST_TIME,'WW'), count(*) no from v$log_history where FIRST_TIME > sysdate - 31 group by trunc(FIRST_TIME,'WW')),(select max(bytes/1024/1024) logsize from v$log);
