set line 200; col "DBF_BACKUP_MB" format 999999.99; col "ARC_BACKUP_MB" format 9999.99; select trunc(completion_time) "BACKUP_DATE", sum(blocks*block_size)/1024/1024 "DBF_BACKUP_MB", (SELECT sum(blocks*block_size)/1024/1024 from v$backup_redolog WHERE first_time > sysdate-1) "ARC_BACKUP_MB" from v$backup_datafile WHERE completion_time > sysdate - 1 group by trunc(completion_time) order by 1 DESC;
select ctime "Date" , decode(backup_type, 'L', 'Archive Log', 'D', 'Full', 'Incremental') backup_type , bsize "Size MB" from (select trunc(bp.completion_time) ctime , backup_type , round(sum(bp.bytes/1024/1024),2) bsize from v$backup_set bs, v$backup_piece bp where bs.set_stamp = bp.set_stamp and bs.set_count = bp.set_count and bp.status = 'A' group by trunc(bp.completion_time), backup_type) order by 1, 2; prompt prompt FILE DETAILS prompt -------------
col MEDIA_HANDLE format a100 wrap SELECT S.RECID AS "BS_REC", P.RECID AS "BP_REC", P.ENCRYPTED, P.TAG, P.HANDLE AS "MEDIA_HANDLE" FROM V$BACKUP_PIECE P, V$BACKUP_SET S WHERE P.SET_STAMP = S.SET_STAMP AND P.SET_COUNT = S.SET_COUNT; prompt col "Datafiles backed up within 24h" format a40; col "Control backed up" format 999; col "SPFiles backed up" format 999; SELECT dbfiles||' out of '||numfiles||' datafiles backed up' "Datafiles backed up within 24h", cfiles "CFiles", spfiles "SPFiles" FROM (select count(*) numfiles from v$datafile), (select count(*) dbfiles from v$backup_datafile a, v$datafile b where a.file# = b.file# and a.completion_time > sysdate - 1), (select count(*) cfiles from v$backup_datafile where file# = 0 and completion_time > sysdate - 1), (select count(*) spfiles from v$backup_spfile where completion_time > sysdate - 1);
prompt prompt prompt "**=====================================================================================================**" prompt** "Workload and I/O Statistics** prompt "**=====================================================================================================**" prompt prompt *** TOP SYSTEM Timed Events (Waits): prompt*-----------------------------------------------------------------------** COLUMN event FORMAT A40 HEADING "Wait Event" TRUNC COLUMN time_waited FORMAT 9999999999999 HEADING "Time|Waited" COLUMN wait_pct FORMAT 9999 HEADING "Wait|(%)" SELECT w.event, w.time_waited, round(w.time_waited/tw.twt*100,2) wait_pct FROM gv$system_event w, (select inst_id, sum(time_waited) twt from gv$system_event where time_waited>0 AND event NOT IN ('Null event', 'client message', 'rdbms ipc reply', 'smon timer', 'rdbms ipc message', 'PX Idle Wait', 'PL/SQL lock timer', 'file open', 'pmon timer', 'WMON goes to sleep', 'virtual circuit status', 'dispatcher timer', 'SQL*Net message from client', 'parallel query dequeue wait', 'pipe get') group by inst_id ) tw WHERE w.inst_id = tw.inst_id and w.time_waited>0 and round(w.time_waited/tw.twt*100,2) > 1 and w.event NOT IN ('Null event', 'client message', 'rdbms ipc reply', 'smon timer', 'rdbms ipc message', 'PX Idle Wait', 'PL/SQL lock timer', 'file open', 'pmon timer', 'WMON goes to sleep', 'virtual circuit status', 'dispatcher timer', 'SQL*Net message from client', 'parallel query dequeue wait', 'pipe get') ORDER by 1;
