col sql_text format a60; col reads_per_exe format 99999999 heading 'reads|per_exe'; col "exe" format 99999; col "sorts" format 99999; col buffer_gets heading 'buffer|gets'; col disk_reads heading 'disk|reads'; SELECT * FROM (SELECT Substr(a.sql_text,1,50) sql_text, Trunc(a.disk_reads/Decode(a.executions,0,1,a.executions)) reads_per_exe, a.buffer_gets, a.disk_reads, a.executions "exe", a.sorts "sorts", a.address "address" FROM v$sqlarea a ORDER BY 2 DESC) WHERE rownum <= 5;
