col name format a30; select * from (select a.sid, c.username, c.osuser, c.machine, logon_time, b.name, a.value from v$sesstat a, v$statname b, v$session c where a.STATISTIC# = b.STATISTIC# and a.sid = c.sid and b.name like '%CPU used by this session%' order by a.value desc) where rownum < 5;
col USERNAME format a20 select 'top_logical_i/o_process', sid, username, total_user_io amt_used, round(100 * total_user_io/total_io,2) pct_used from (select b.sid sid, nvl(b.username, p.name) username, sum(value) total_user_io from v$statname c, v$sesstat a, v$session b, v$bgprocess p where a.statistic# = c.statistic# and p.paddr (+) = b.paddr and b.sid = a.sid and c.name in ('consistent gets', 'db block gets') and b.username not in ('SYS', 'SYSTEM', 'SYSMAN', 'DBSNMP') group by b.sid, nvl(b.username, p.name) order by 3 desc), (select sum(value) total_io from v$statname c, v$sesstat a, v$session b, v$bgprocess p where a.statistic# = c.statistic# and p.paddr (+) = b.paddr and b.sid = a.sid and c.name in ('consistent gets', 'db block gets')) where rownum < 2 union all select 'top memory process', sid, username, total_user_mem, round(100 * total_user_mem/total_mem,2) from (select b.sid sid, nvl(b.username, p.name) username, sum(value) total_user_mem from v$statname c, v$sesstat a, v$session b, v$bgprocess p where a.statistic# = c.statistic# and p.paddr (+) = b.paddr and b.sid = a.sid and c.name in ('session pga memory', 'session uga memory') and b.username not in ('SYS', 'SYSTEM', 'SYSMAN', 'DBSNMP') group by b.sid, nvl(b.username, p.name) order by 3 desc), (select sum(value) total_mem from v$statname c, v$sesstat a where a.statistic# = c.statistic# and c.name in ('session pga memory', 'session uga memory')) where rownum < 2 union all select 'top cpu process', sid, username, total_user_cpu, round(100 * total_user_cpu/greatest(total_cpu,1),2) from (select b.sid sid, nvl(b.username, p.name) username, sum(value) total_user_cpu from v$statname c, v$sesstat a, v$session b, v$bgprocess p where a.statistic# = c.statistic# and p.paddr (+) = b.paddr and b.sid = a.sid and c.name = 'CPU used by this session' and b.username not in ('SYS', 'SYSTEM', 'SYSMAN', 'DBSNMP') group by b.sid, nvl(b.username, p.name) order by 3 desc), (select sum(value) total_cpu from v$statname c, v$sesstat a, v$session b, v$bgprocess p where a.statistic# = c.statistic# and p.paddr (+) = b.paddr and b.sid = a.sid and c.name = 'CPU used by this session') where rownum < 2;
